import React from 'react';
import './../style/container.css'
import {MdFavorite} from "react-icons/md";
import { useState } from 'react';

const Card = ({card})  => {
   const favListColor = [ {color:'#ddd'},  {color:'#F20'}]
   const btnColor = [ 
    {color:'#fff',fontWeight:700,backgroundColor:'	#DB7093'}, 
    {color:'#fff',fontWeight:700,backgroundColor:'	#DDA0DD	'}
]
   const [fav,setFav]  = useState(0);
   const [checkState, setCheckState]= useState('')
   const fncFav = (e) => {
    setFav((fav === 0) ? 1 : 0) ; 
    setCheckState(e.target.checked)
   }

    return (
        <li>
        <div className='img_set' style={{background:`url(${card.imgUrl}) no-repeat center / cover`}}></div>
        <dl>
           <dt>{card.title}</dt>
           <dd>{card.content}</dd>
        </dl>
        <div className='btns'>
           <input type='checkbox' 
           id={card.checkName} 
           name={card.checkName} 
           data-checked={checkState}
           onChange={fncFav}
           className="blind" 
            />
           <label 
           htmlFor={card.checkName} 
           style={btnColor[fav]}>
            좋아요
            <MdFavorite style={favListColor[fav]} />
           </label>
           <a className='link' href={card.dataLink}>상세보기</a>
       </div>
     </li>
    );
};

export default Card;
